import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useContent } from '../context/ContentContext';
import { useUserProgress } from '../context/UserProgressContext';

const AdminDashboard = () => {
  const navigate = useNavigate();
  const { isAdmin } = useAuth();
  const { properties, practiceProblems, challengeQuestions, updateContent } = useContent();
  const { usersProgress, generateReport, exportData } = useUserProgress();
  const [activeTab, setActiveTab] = useState('progress');
  const [selectedUser, setSelectedUser] = useState(null);
  const [customChallenge, setCustomChallenge] = useState({
    title: '',
    description: '',
    difficulty: 'medium',
    points: 100,
    deadline: ''
  });

  if (!isAdmin()) {
    navigate('/');
    return null;
  }

  const handleUpdate = (type, id, updatedData) => {
    updateContent(type, id, updatedData);
  };

  const handleDelete = (type, id) => {
    if (window.confirm('Are you sure you want to delete this item?')) {
      updateContent(type, id, null);
    }
  };

  const handleAdd = (type, newData) => {
    updateContent(type, null, newData);
  };

  const handleCreateCustomChallenge = () => {
    // Add the custom challenge to the challenge questions
    handleAdd('challenge', {
      ...customChallenge,
      question: customChallenge.title,
      answers: ['A', 'B', 'C', 'D'],
      correctAnswer: 'A',
      explanation: customChallenge.description,
      property: 'Custom Challenge',
      isCustom: true
    });
    setCustomChallenge({
      title: '',
      description: '',
      difficulty: 'medium',
      points: 100,
      deadline: ''
    });
  };

  const downloadReport = (userId) => {
    const report = generateReport(userId);
    const dataStr = JSON.stringify(report, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    const exportFileDefaultName = `student-report-${userId}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  const downloadAllData = () => {
    const data = exportData();
    const dataStr = JSON.stringify(data, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    const exportFileDefaultName = `all-student-data-${new Date().toISOString().split('T')[0]}.json`;

    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-purple-100 py-12 px-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-blue-800 mb-8 text-center">
          Admin Dashboard üõ†Ô∏è
        </h1>

        {/* Tabs */}
        <div className="flex space-x-4 mb-8 justify-center">
          <button
            onClick={() => setActiveTab('progress')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'progress'
                ? 'bg-blue-600 text-white'
                : 'bg-white text-gray-600 hover:bg-blue-50'
            }`}
          >
            Student Progress
          </button>
          <button
            onClick={() => setActiveTab('custom')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'custom'
                ? 'bg-blue-600 text-white'
                : 'bg-white text-gray-600 hover:bg-blue-50'
            }`}
          >
            Custom Challenges
          </button>
          <button
            onClick={() => setActiveTab('content')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'content'
                ? 'bg-blue-600 text-white'
                : 'bg-white text-gray-600 hover:bg-blue-50'
            }`}
          >
            Content Management
          </button>
        </div>

        {/* Content */}
        <div className="bg-white rounded-lg shadow-lg p-6">
          {activeTab === 'progress' && (
            <div>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-2xl font-bold">Student Progress Monitoring</h2>
                <button
                  onClick={downloadAllData}
                  className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded"
                >
                  Export All Data
                </button>
              </div>
              <div className="overflow-x-auto">
                <table className="min-w-full">
                  <thead>
                    <tr className="bg-gray-50">
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Student</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Points</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Progress</th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {usersProgress.map((user) => {
                      const report = generateReport(user.userId);
                      return (
                        <tr key={user.userId}>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {user.userId}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            {user.points}
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            <div className="w-full bg-gray-200 rounded-full h-2.5">
                              <div
                                className="bg-blue-600 h-2.5 rounded-full"
                                style={{ width: `${report.progressPercentage}%` }}
                              ></div>
                            </div>
                            <span className="text-xs text-gray-500">{Math.round(report.progressPercentage)}%</span>
                          </td>
                          <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                            <button
                              onClick={() => downloadReport(user.userId)}
                              className="text-blue-600 hover:text-blue-800"
                            >
                              Download Report
                            </button>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            </div>
          )}

          {activeTab === 'custom' && (
            <div>
              <h2 className="text-2xl font-bold mb-4">Create Custom Challenge</h2>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Title</label>
                  <input
                    type="text"
                    value={customChallenge.title}
                    onChange={(e) => setCustomChallenge({ ...customChallenge, title: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Description</label>
                  <textarea
                    value={customChallenge.description}
                    onChange={(e) => setCustomChallenge({ ...customChallenge, description: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    rows="3"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Difficulty</label>
                    <select
                      value={customChallenge.difficulty}
                      onChange={(e) => setCustomChallenge({ ...customChallenge, difficulty: e.target.value })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    >
                      <option value="easy">Easy</option>
                      <option value="medium">Medium</option>
                      <option value="hard">Hard</option>
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700">Points</label>
                    <input
                      type="number"
                      value={customChallenge.points}
                      onChange={(e) => setCustomChallenge({ ...customChallenge, points: parseInt(e.target.value) })}
                      className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    />
                  </div>
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Deadline</label>
                  <input
                    type="datetime-local"
                    value={customChallenge.deadline}
                    onChange={(e) => setCustomChallenge({ ...customChallenge, deadline: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                  />
                </div>
                <button
                  onClick={handleCreateCustomChallenge}
                  className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                >
                  Create Challenge
                </button>
              </div>
            </div>
          )}

          {activeTab === 'content' && (
            <div>
              <h2 className="text-2xl font-bold mb-4">Content Management</h2>
              {/* Existing content management code */}
              {/* ... */}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
