import React, { createContext, useState, useContext, useEffect } from 'react';

const UserProgressContext = createContext();

export const useUserProgress = () => useContext(UserProgressContext);

export const UserProgressProvider = ({ children }) => {
  const [usersProgress, setUsersProgress] = useState([]);
  const [badges, setBadges] = useState([]);
  const [loading, setLoading] = useState(true);

  // Initial badges
  const initialBadges = [
    { id: 1, name: "Quick Learner", description: "Complete 5 practice problems", icon: "âš¡", points: 100 },
    { id: 2, name: "Master of Properties", description: "Score 100% on a challenge", icon: "ðŸ†", points: 200 },
    { id: 3, name: "Practice Makes Perfect", description: "Complete 10 practice problems", icon: "ðŸ“š", points: 300 },
    { id: 4, name: "Challenge Champion", description: "Complete 5 challenges", icon: "ðŸŽ¯", points: 400 },
    { id: 5, name: "Property Pro", description: "Master all properties", icon: "ðŸŒŸ", points: 500 }
  ];

  useEffect(() => {
    setBadges(initialBadges);
    setLoading(false);
  }, []);

  const updateUserProgress = (userId, progress) => {
    setUsersProgress(prev => {
      const existingUser = prev.find(u => u.userId === userId);
      if (existingUser) {
        return prev.map(u => u.userId === userId ? { ...u, ...progress } : u);
      }
      return [...prev, { userId, ...progress }];
    });
  };

  const getUserProgress = (userId) => {
    return usersProgress.find(u => u.userId === userId) || {
      userId,
      points: 0,
      completedPractice: 0,
      completedChallenges: 0,
      badges: [],
      lastActivity: null
    };
  };

  const getLeaderboard = () => {
    return [...usersProgress]
      .sort((a, b) => b.points - a.points)
      .slice(0, 10);
  };

  const awardBadge = (userId, badgeId) => {
    const userProgress = getUserProgress(userId);
    if (!userProgress.badges.includes(badgeId)) {
      const badge = badges.find(b => b.id === badgeId);
      updateUserProgress(userId, {
        ...userProgress,
        badges: [...userProgress.badges, badgeId],
        points: userProgress.points + badge.points
      });
    }
  };

  const generateReport = (userId) => {
    const userProgress = getUserProgress(userId);
    return {
      ...userProgress,
      earnedBadges: badges.filter(b => userProgress.badges.includes(b.id)),
      progressPercentage: Math.min(
        ((userProgress.completedPractice + userProgress.completedChallenges) / 20) * 100,
        100
      )
    };
  };

  const exportData = () => {
    return {
      usersProgress,
      badges,
      timestamp: new Date().toISOString()
    };
  };

  const value = {
    usersProgress,
    badges,
    updateUserProgress,
    getUserProgress,
    getLeaderboard,
    awardBadge,
    generateReport,
    exportData,
    loading
  };

  return (
    <UserProgressContext.Provider value={value}>
      {!loading && children}
    </UserProgressContext.Provider>
  );
};
