import React, { createContext, useState, useContext, useEffect } from 'react';
import { useAuth } from './AuthContext';

const ContentContext = createContext();

export const useContent = () => useContext(ContentContext);

export const ContentProvider = ({ children }) => {
  const { isAdmin } = useAuth();
  const [properties, setProperties] = useState([]);
  const [practiceProblems, setPracticeProblems] = useState([]);
  const [challengeQuestions, setChallengeQuestions] = useState([]);
  const [loading, setLoading] = useState(true);

  // Initial content data
  const initialProperties = [
    {
      id: 1,
      name: "Commutative Property",
      formula: "a × b = b × a",
      description: "The order of factors doesn't change the product.",
      example: "5 × 3 = 3 × 5 = 15",
      icon: "🔄"
    },
    {
      id: 2,
      name: "Associative Property",
      formula: "(a × b) × c = a × (b × c)",
      description: "The way factors are grouped doesn't change the product.",
      example: "(2 × 3) × 4 = 2 × (3 × 4) = 24",
      icon: "🔗"
    },
    {
      id: 3,
      name: "Distributive Property",
      formula: "a × (b + c) = a × b + a × c",
      description: "Multiplying a sum by a number is the same as multiplying each addend by the number and then adding the products.",
      example: "3 × (4 + 2) = 3 × 4 + 3 × 2 = 12 + 6 = 18",
      icon: "📦"
    },
    {
      id: 4,
      name: "Identity Property",
      formula: "a × 1 = a",
      description: "Any number multiplied by 1 equals itself.",
      example: "7 × 1 = 7",
      icon: "🆔"
    },
    {
      id: 5,
      name: "Zero Property",
      formula: "a × 0 = 0",
      description: "Any number multiplied by 0 equals 0.",
      example: "9 × 0 = 0",
      icon: "0️⃣"
    }
  ];

  const initialPracticeProblems = [
    {
      id: 1,
      problem: "Use the commutative property to rewrite: 7 × 4",
      answer: "4 × 7",
      hint: "The commutative property allows you to change the order of factors.",
      property: "Commutative Property"
    },
    {
      id: 2,
      problem: "Use the associative property to rewrite: (3 × 2) × 5",
      answer: "3 × (2 × 5)",
      hint: "The associative property allows you to change the grouping of factors.",
      property: "Associative Property"
    },
    {
      id: 3,
      problem: "Use the distributive property to rewrite: 6 × (3 + 2)",
      answer: "(6 × 3) + (6 × 2)",
      hint: "The distributive property allows you to multiply each addend separately.",
      property: "Distributive Property"
    },
    {
      id: 4,
      problem: "What is 9 × 1? (Use the identity property)",
      answer: "9",
      hint: "The identity property states that any number multiplied by 1 equals itself.",
      property: "Identity Property"
    },
    {
      id: 5,
      problem: "What is 5 × 0? (Use the zero property)",
      answer: "0",
      hint: "The zero property states that any number multiplied by 0 equals 0.",
      property: "Zero Property"
    }
  ];

  const initialChallengeQuestions = [
    {
      id: 1,
      question: "If 6 × (3 + 4) = (6 × 3) + (6 × 4), which property is being demonstrated?",
      property: "Distributive Property",
      answers: ["Commutative Property", "Associative Property", "Distributive Property", "Identity Property"],
      correctAnswer: "Distributive Property",
      explanation: "The distributive property allows us to multiply a sum by multiplying each addend separately and then adding the products."
    },
    {
      id: 2,
      question: "What is the value of (5 × 2) × 3?",
      property: "Associative Property",
      answers: ["15", "30", "25", "20"],
      correctAnswer: "30",
      explanation: "Using the associative property: (5 × 2) × 3 = 10 × 3 = 30"
    },
    {
      id: 3,
      question: "If a × b = b × a, which property is being demonstrated?",
      property: "Commutative Property",
      answers: ["Commutative Property", "Associative Property", "Distributive Property", "Identity Property"],
      correctAnswer: "Commutative Property",
      explanation: "The commutative property states that changing the order of factors doesn't change the product."
    },
    {
      id: 4,
      question: "What is 8 × (2 + 3) equal to?",
      property: "Distributive Property",
      answers: ["40", "24", "16", "48"],
      correctAnswer: "40",
      explanation: "Using the distributive property: 8 × (2 + 3) = (8 × 2) + (8 × 3) = 16 + 24 = 40"
    },
    {
      id: 5,
      question: "What property states that any number multiplied by 1 equals itself?",
      property: "Identity Property",
      answers: ["Commutative Property", "Associative Property", "Identity Property", "Zero Property"],
      correctAnswer: "Identity Property",
      explanation: "The identity property of multiplication states that any number multiplied by 1 equals itself."
    }
  ];

  useEffect(() => {
    // Load initial content
    setProperties(initialProperties);
    setPracticeProblems(initialPracticeProblems);
    setChallengeQuestions(initialChallengeQuestions);
    setLoading(false);
  }, []);

  const updateContent = (type, id, data) => {
    if (!isAdmin()) return;

    switch (type) {
      case 'properties':
        if (id === null) {
          // Add new property
          const newId = Math.max(...properties.map(p => p.id)) + 1;
          setProperties([...properties, { ...data, id: newId }]);
        } else if (data === null) {
          // Delete property
          setProperties(properties.filter(p => p.id !== id));
        } else {
          // Update property
          setProperties(properties.map(p => p.id === id ? { ...p, ...data } : p));
        }
        break;

      case 'practice':
        if (id === null) {
          // Add new problem
          const newId = Math.max(...practiceProblems.map(p => p.id)) + 1;
          setPracticeProblems([...practiceProblems, { ...data, id: newId }]);
        } else if (data === null) {
          // Delete problem
          setPracticeProblems(practiceProblems.filter(p => p.id !== id));
        } else {
          // Update problem
          setPracticeProblems(practiceProblems.map(p => p.id === id ? { ...p, ...data } : p));
        }
        break;

      case 'challenge':
        if (id === null) {
          // Add new question
          const newId = Math.max(...challengeQuestions.map(q => q.id)) + 1;
          setChallengeQuestions([...challengeQuestions, { ...data, id: newId }]);
        } else if (data === null) {
          // Delete question
          setChallengeQuestions(challengeQuestions.filter(q => q.id !== id));
        } else {
          // Update question
          setChallengeQuestions(challengeQuestions.map(q => q.id === id ? { ...q, ...data } : q));
        }
        break;

      default:
        break;
    }
  };

  const value = {
    properties,
    practiceProblems,
    challengeQuestions,
    updateContent,
    loading
  };

  return (
    <ContentContext.Provider value={value}>
      {!loading && children}
    </ContentContext.Provider>
  );
};

export default ContentContext;
