import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useUserProgress } from '../context/UserProgressContext';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const ChallengePage = () => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { addPoints, awardBadge } = useUserProgress();
  const [currentChallenge, setCurrentChallenge] = useState(0);
  const [lives, setLives] = useState(3);
  const [score, setScore] = useState(0);
  const [showFeedback, setShowFeedback] = useState(false);
  const [feedbackMessage, setFeedbackMessage] = useState('');
  const [isCorrect, setIsCorrect] = useState(null);
  const [badgesEarned, setBadgesEarned] = useState([]);

  useEffect(() => {
    if (!currentUser) {
      navigate('/login');
    }
  }, [currentUser, navigate]);

  const challenges = [
    {
      id: 1,
      scenario: "Liza has 3 baskets with 4 apples each. She wants to know how many apples she has in total.",
      question: "Which property would help Liza calculate 3 × 4?",
      correctAnswer: "Commutative",
      explanation: "Liza can use the Commutative Property to calculate 3 × 4 as 4 × 3, which might be easier for her to visualize."
    },
    {
      id: 2,
      scenario: "Tom is organizing his toy cars. He has 2 boxes, each containing 3 rows of 5 cars.",
      question: "Which property would help Tom calculate (2 × 3) × 5?",
      correctAnswer: "Associative",
      explanation: "Tom can use the Associative Property to group the multiplication differently: 2 × (3 × 5)."
    },
    {
      id: 3,
      scenario: "Sarah has 5 bags of marbles. Each bag has 1 special marble.",
      question: "Which property explains why 5 × 1 = 5?",
      correctAnswer: "Identity",
      explanation: "The Identity Property shows that any number multiplied by 1 equals itself."
    }
  ];

  const dragDropQuestions = [
    {
      id: 'q1',
      expression: '4 × 5 = 5 × 4',
      correctProperty: 'Commutative'
    },
    {
      id: 'q2',
      expression: '(2 × 3) × 4 = 2 × (3 × 4)',
      correctProperty: 'Associative'
    },
    {
      id: 'q3',
      expression: '7 × 1 = 7',
      correctProperty: 'Identity'
    }
  ];

  const [dragDropState, setDragDropState] = useState({
    questions: dragDropQuestions,
    properties: {
      'Commutative': [],
      'Associative': [],
      'Identity': []
    }
  });

  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const { source, destination } = result;
    const question = dragDropQuestions.find(q => q.id === result.draggableId);
    
    if (destination.droppableId === question.correctProperty) {
      setScore(score + 10);
      setFeedbackMessage('✅ Correct! Great job!');
      setIsCorrect(true);
      
      // Update state
      const newState = { ...dragDropState };
      newState.properties[destination.droppableId] = [
        ...newState.properties[destination.droppableId],
        question
      ];
      setDragDropState(newState);
      
      // Check if all questions are correctly placed
      if (Object.values(newState.properties).flat().length === dragDropQuestions.length) {
        awardBadge(currentUser.email, 5); // "Property Pro" badge
        setBadgesEarned(prev => [...prev, 'Property Pro']);
      }
    } else {
      setLives(lives - 1);
      setFeedbackMessage('❌ Oops! Try again!');
      setIsCorrect(false);
    }
    
    setShowFeedback(true);
    setTimeout(() => setShowFeedback(false), 1500);
  };

  const handleScenarioAnswer = (answer) => {
    const current = challenges[currentChallenge];
    const correct = answer === current.correctAnswer;
    
    if (correct) {
      setScore(score + 20);
      setFeedbackMessage('✅ Correct! ' + current.explanation);
      setIsCorrect(true);
      
      if (score + 20 >= 100) {
        awardBadge(currentUser.email, 6); // "Multiplication Master" badge
        setBadgesEarned(prev => [...prev, 'Multiplication Master']);
      }
    } else {
      setLives(lives - 1);
      setFeedbackMessage('❌ Not quite! ' + current.explanation);
      setIsCorrect(false);
    }
    
    setShowFeedback(true);
    setTimeout(() => {
      setShowFeedback(false);
      if (currentChallenge < challenges.length - 1 && lives > 0) {
        setCurrentChallenge(currentChallenge + 1);
      }
    }, 2000);
  };

  if (lives === 0) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-blue-100 to-purple-100 flex items-center justify-center p-4">
        <div className="bg-white p-8 rounded-lg shadow-lg max-w-md text-center">
          <h2 className="text-2xl font-bold text-red-600 mb-4">Game Over!</h2>
          <p className="text-xl mb-4">Final Score: {score}</p>
          {badgesEarned.length > 0 && (
            <div className="mb-4">
              <h3 className="text-lg font-bold mb-2">Badges Earned:</h3>
              <div className="flex justify-center space-x-2">
                {badgesEarned.map(badge => (
                  <span key={badge} className="bg-yellow-100 px-3 py-1 rounded-full">
                    {badge}
                  </span>
                ))}
              </div>
            </div>
          )}
          <button
            onClick={() => {
              setLives(3);
              setScore(0);
              setCurrentChallenge(0);
              setBadgesEarned([]);
            }}
            className="bg-blue-500 text-white px-6 py-2 rounded-lg hover:bg-blue-600"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-purple-100">
      <div className="max-w-4xl mx-auto py-8 px-4">
        <div className="flex justify-between items-center mb-8">
          <div className="flex items-center space-x-4">
            <div className="bg-red-100 px-4 py-2 rounded-lg">
              <span className="text-red-600 font-bold">❤️ Lives: {lives}</span>
            </div>
            <div className="bg-blue-100 px-4 py-2 rounded-lg">
              <span className="text-blue-600 font-bold">⭐ Score: {score}</span>
            </div>
          </div>
          {badgesEarned.length > 0 && (
            <div className="flex space-x-2">
              {badgesEarned.map(badge => (
                <span key={badge} className="bg-yellow-100 px-3 py-1 rounded-full">
                  {badge}
                </span>
              ))}
            </div>
          )}
        </div>

        {showFeedback && (
          <div className={`mb-4 p-4 rounded-lg text-center ${
            isCorrect ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            {feedbackMessage}
          </div>
        )}

        <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
          {/* Scenario Challenge */}
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4">Scenario Challenge</h3>
            <p className="mb-4">{challenges[currentChallenge].scenario}</p>
            <p className="font-bold mb-4">{challenges[currentChallenge].question}</p>
            <div className="grid grid-cols-1 gap-2">
              {['Commutative', 'Associative', 'Identity'].map(property => (
                <button
                  key={property}
                  onClick={() => handleScenarioAnswer(property)}
                  className="p-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                >
                  {property}
                </button>
              ))}
            </div>
          </div>

          {/* Drag and Drop Challenge */}
          <div className="bg-white p-6 rounded-lg shadow-lg">
            <h3 className="text-xl font-bold mb-4">Drag and Drop Challenge</h3>
            <p className="mb-4">Match each expression with its property!</p>
            <DragDropContext onDragEnd={handleDragEnd}>
              <div className="grid grid-cols-3 gap-4">
                {Object.entries(dragDropState.properties).map(([property, questions]) => (
                  <Droppable key={property} droppableId={property}>
                    {(provided) => (
                      <div
                        ref={provided.innerRef}
                        {...provided.droppableProps}
                        className="bg-gray-100 p-4 rounded-lg min-h-[200px]"
                      >
                        <h4 className="font-bold mb-2">{property}</h4>
                        {questions.map((question, index) => (
                          <div
                            key={question.id}
                            className="bg-white p-2 mb-2 rounded"
                          >
                            {question.expression}
                          </div>
                        ))}
                        {provided.placeholder}
                      </div>
                    )}
                  </Droppable>
                ))}
              </div>
              <div className="mt-4">
                <h4 className="font-bold mb-2">Expressions to Match:</h4>
                <div className="grid grid-cols-1 gap-2">
                  {dragDropState.questions.map((question, index) => (
                    <Draggable key={question.id} draggableId={question.id} index={index}>
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="bg-blue-100 p-3 rounded-lg cursor-move"
                        >
                          {question.expression}
                        </div>
                      )}
                    </Draggable>
                  ))}
                </div>
              </div>
            </DragDropContext>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChallengePage;
